mapper.select:
SELECT		*
FROM		emp
WHERE		empno = #{empno}

SELECT		passanswer
FROM		logininfo
WHERE		empno=#{empno} and password=#{password}

SELECT		*
FROM		logininfo
WHERE		passquiz = #{passQuiz} and passanswer=#{passAnswer} and empno=#{empno}

SELECT		*
FROM		Dept
where deptno = #{deptno}

